const typingSpeed = 150; // 调整为150毫秒，使打字速度更慢

function getCurrentIndex() {
    const currentTime = Date.now(); // 获取当前时间
    const elapsedTime = currentTime - new Date('2024-08-31T20:51:00Z').getTime(); // 计算已过时间
    const index = Math.floor(elapsedTime / typingSpeed) % text.length; // 根据时间和打字速度计算应显示的字符数
    return index;
}

function typeText() {
    const index = getCurrentIndex();
    const currentText = text.substring(0, index + 1)
                            .replace(/\n/g, "<br>")  // 替换换行符为 <br>
                            .replace(/^\s+/, ""); // 移除文本开头的所有空格
    document.getElementById("typed-text").innerHTML = currentText; // 使用 innerHTML 替换内容

    // 自动滚动到页面底部，确保最新内容可见
    window.scrollTo(0, document.body.scrollHeight);

    setTimeout(typeText, typingSpeed);
}

document.addEventListener("DOMContentLoaded", typeText);